	(36849, 0, 0, '%s attempts to run away in fear!', 16, 0, 100, 0, 0, 0, 'combat Flee'),
	(36853, 0, 0, 'You are fools to have come to this place! The icy winds of Northrend will consume your souls!', 14, 0, 0, 0, 0, 17007, 'Sindragosa - SAY_AGGRO'),
	(36853, 1, 0, 'Suffer, mortals, as your pathetic magic betrays you!', 14, 0, 0, 0, 0, 17014, 'Sindragosa - SAY_UNCHAINED_MAGIC'),
	(36853, 2, 0, '%s prepares to unleash a wave of blistering cold!', 41, 0, 0, 0, 0, 0, 'Sindragosa - EMOTE_WARN_BLISTERING_COLD'),
	(36853, 3, 0, 'Can you feel the cold hand of death upon your heart?', 14, 0, 0, 0, 0, 17013, 'Sindragosa - SAY_BLISTERING_COLD'),
	(36853, 4, 0, 'Aaah! It burns! What sorcery is this?!', 14, 0, 0, 0, 0, 17015, 'Sindragosa - SAY_RESPITE_FOR_A_TORMENTED_SOUL'),
	(36853, 5, 0, 'Your incursion ends here! None shall survive!', 14, 0, 0, 0, 0, 17012, 'Sindragosa - SAY_AIR_PHASE'),
