	(17077, 1, 0, '%s lets out a howl that rings across the mountains to the north and motions for you to follow.', 16, 0, 100, 0, 0, 0, 'ancestral wolf EMOTE_WOLF_HOWL'),
	(17023, 0, 0, 'Gul\'dan speaks the truth! We should return at once to tell our brothers of the news! Retreat back through the portal!', 14, 0, 100, 0, 0, 0, 'ancestral wolf SAY_WOLF_WELCOME'),
	(16993, 0, 0, 'Thank you for agreeing to help. Now, let\'s get out of here $N.', 12, 0, 100, 0, 0, 0, 'wounded elf SAY_ELF_START'),
